import React, { useEffect, useState, useRef, useContext } from 'react';
import AuthContext from "./context/AuthProvider";
import { Redirect } from 'react-router-dom';
import { Link } from 'react-router-dom';
import axios from './api/axios';



function LoginForm(props) {
  const { setAuth } = useContext(AuthContext);
  const userRef = useRef();
  const errRef = useRef();

  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [redirect, setRedirect] = useState(false)
  const [role, setRole] = useState('')

  const [errMsg, setErrMsg] = useState('');
  const [success, setSuccess] = useState(false);

  useEffect(() => {
    setErrMsg('');
  }, [email, password])



  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = {
      email: email,
      password: password
    }

    try {
      const response = await axios.post('/api/authmanagement/login',
        data,
        {
          headers: { 'Content-Type': 'application/json' },
          withCredentials: true
        }
      );
      console.log(response?.data)
      console.log(response?.data?.token)
      const accessToken = response?.data?.token;
      setAuth({ email, password, roles, accessToken});
      setSuccess(true);
    } catch (err) {

    }

  }

  return (
    <>
      {success ? (
        <div className="alert alert-success" role="alert">
          <h1>Jeni Kyqur Me Sukses!</h1>
          <Link to='/' className="alert-link">Shko ne Balline</Link>
        </div>
      ) : (
        <div className="d-flex justify-content-center">
          <form onSubmit={handleSubmit} autoComplete="off">
            <div className="w-90">
              {/*Alert Box*/}
              <div ref={errRef} className={errMsg ? "alert alert-danger" : "collapse"} role="alert" aria-live="assertive">
                {errMsg}
              </div>
              <h2>Login</h2>
              <div className="form-outline mb-8">
                <input
                  type="email"
                  id="email"
                  className="form-control"
                  name="email"
                  required
                  onChange={(e) => setEmail(e.target.value)}
                />
                <label className="form-label" for="email">
                  Email address
                </label>
              </div>

              <div className="form-outline mb-4">
                <input
                  type="password"
                  id="password"
                  className="form-control"
                  name="password"
                  required
                  onChange={(e) => setPassword(e.target.value)}
                />
                <label className="form-label" for="password">
                  Password
                </label>
              </div>
              <button
                type="submit"
                className="btn btn-primary btn-block mb-4"
              >
                Sign in
              </button>
            </div>
          </form>
        </div>
      )}
    </>
  )

}

export default LoginForm;
