import React, { useState } from 'react'

function LoginForm({ Login, error }) {
    const [details, setDetails] = useState({ name: "", email: "", password: "" });
    const submitHandler = e => {
        e.preventDefault();
        Login(details);
    }
    return (
        <div>
            <form onSubmit={submitHandler}>
                <div className="form-inner w-75 vw-100 vh-100 d-flex align-items-center justify-content-center">
                    <h2>Login</h2>
                    {(error != "") ? (<div className='error'>{error}</div>) : ""}
                    <div className='form-floating mb-3'>
                        <input type="email" className="form-control" name="email" id="email" onChange={e => setDetails({ ...details, email: e.target.value })} value={details.email} />
                        <label for="email">Email address</label>
                    </div>
                    <div className='form-floating mb-3'>
                        <input type="password" className="form-control" name="password" id="password" onChange={e => setDetails({ ...details, password: e.target.value })} value={details.password} />
                        <label for="password">Password</label>
                    </div>
                    <input className="btn btn-primary btn-block form-control" type="submit" value="LOGIN" />
                </div>
            </form>
        </div>
    )
}

export default LoginForm;
