import useAuth from "./useAuth";

const RequireAuth = ({ allowedRoles }) => {
    const { auth } = useAuth();
    const location = useLocation();

    return (
        auth?.roles?.f
            ?<Outlet />
            :<Navigate to="/" state={{from : location}} replace />
    )
}

export default RequireAuth;
