import React, { Component } from 'react';
import { Table } from 'react-bootstrap';
import { Button, ButtonToolbar } from 'react-bootstrap';
//import { AddPijaModal } from './AddPijaModal';
//import { EditPijaModal } from './EditPijaModal';
import { Navigation } from './Navigation';


export class Lagje extends Component {
    constructor(props) {
        super(props);
        this.state = { lagjet: [], addModalShow: false, editModalShow: false }
    }

    //marrja e te dhenave nga API
    refreshList() {
        fetch(process.env.REACT_APP_API + 'lagje')
            .then(response => response.json())
            .then(data => {
                this.setState({ lagjet: data });
            });
    }

    componentDidMount() {
        this.refreshList();
    }

    componentDidUpdate() {
        this.refreshList();
    }

    //delete a restaurant
    deleteLagje(id) {
        if (window.confirm('A doni ta fshini lagjen?')) {
            fetch(process.env.REACT_APP_API + 'lagje/' + id, {
                method: 'DELETE',
                header: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                }
            })
        }
    }

    render() {
        const { lagje, lagjeID, emri} = this.state;
        let addModalClose = () => this.setState({ addModalShow: false });
        let editModalClose = () => this.setState({ editModalShow: false });
        return (
            <div className="lagje mt-5">
                <Navigation />
                <Table className="mt-4" striped bordered hover size="sm">
                    <thead>
                        <tr>
                            <th>Id</th>
                            <th>Emri</th>
                        </tr>
                    </thead>
                    <tbody>
                        {lagjet.map(el =>
                            <tr key={el.lagjeID}>
                                <td>{el.lagjeID}</td>
                                <td>{el.emri}</td>
                                <td>
                                    <ButtonToolbar>
                                        <Button className="mr-2" variant="info"
                                            onClick={() => this.setState({
                                                editModalShow: true,
                                                lagjeID: el.lagjeID, emri: el.emri
                                            })}>
                                            Edit
                                        </Button>

                                        <Button className="mr-2" variant="danger"
                                            onClick={() => this.deleteLagje(el.lagjeID)}>
                                            Delete
                                        </Button>
                                        

                                    </ButtonToolbar>

                                </td>

                            </tr>)}
                    </tbody>

                </Table>
                <ButtonToolbar>
                    <div className="d-grid gap-2">
                        <Button variant='primary' size='lg'
                            onClick={() => { this.setState({ addModalShow: true }) }}>
                            Shto Pije
                        </Button>
                    </div>
                </ButtonToolbar>
            </div>
        )
    }
}
