import { Link } from "react-router-dom";
import React, { Component } from "react";
import { Navigation } from './Navigation';
import { AddRestorantModal } from './AddRestorantModal';

export class ShfletoRestaurantet extends Component {
    constructor(props) {
        super(props);
        this.state = { restorantet: [], addModalShow: false, editModalShow: false }
    }

    //marrja e te dhenave nga API
    refreshList() {
        fetch(process.env.REACT_APP_API + 'restaurant')
            .then(response => response.json())
            .then(data => {
                this.setState({ restorantet: data });
            });
    }

    componentDidMount() {
        this.refreshList();
    }

    componentDidUpdate() {
        this.refreshList();
    }


    render() {
        const { restorantet, restaurantID, emri, data_regjistrimit, nrYjeve, foto, pershkrimi } = this.state;
        let addModalClose = () => this.setState({ addModalShow: false });
        return (
            <><ListGroup as="ul">
                <ListGroup.Item as="li" active>
                    Cras justo odio
                </ListGroup.Item>
                <ListGroup.Item as="li">Dapibus ac facilisis in</ListGroup.Item>
                <ListGroup.Item as="li" disabled>
                    Morbi leo risus
                </ListGroup.Item>
                <ListGroup.Item as="li">Porta ac consectetur ac</ListGroup.Item>
            </ListGroup>

                <div className="restaurant">
                    <Navigation />
                    <div className="row row-cols-1 row-cols-md-3 g-4">
                        {restorantet.map(el => <div className="col">
                            <div className="card h-100">
                                <img src="https://mdbcdn.b-cdn.net/img/new/standard/city/044.webp" className="card-img-top" alt="Skyscrapers" />
                                <div className="card-body">
                                    <h5 className="card-title">{el.emri}</h5>
                                    <small>{el.nrYjeve} yje</small>
                                    <p className="card-text">{el.pershkrimi}</p>
                                </div>
                                <button type="button" value="LOGIN" class="btn btn-secondary btn-block">Zgjedh</button>
                                <div className="card-footer">
                                    <small className="text-muted">{el.data_regjistrimit}</small>
                                </div>
                            </div>
                        </div>
                        )}
                    </div>
                </div></>
        );
    }
}
