import React,{Component} from 'react';
import {Modal,Button, Row, Col, Form} from 'react-bootstrap';

export class AddRestorantModal extends Component{
    constructor(props){
        super(props);
        this.handleSubmit=this.handleSubmit.bind();
    }

    handleSubmit(event){
        //const dt = new Date();
        //let date = dt.getFullYear()+'-'+(dt.getMonth()+1)+'-'+dt.getDate();
        event.preventDefault();
        fetch(process.env.REACT_APP_API+'restaurant',{
            method:'POST',
            headers:{
                'Accept':'application/json',
                'Content-Type':'application/json'
            },
            body: JSON.stringify({
                restaurantID: event.target.restaurantID.value,
                data_regjistrimit: event.target.data_regjistrimit.value,
                perdoruesiID: event.target.perdoruesiID.value
            })
        })
        .then(res=>res.json())
        .then((result)=>{
            alert(result);
        },
        (error)=>{
            alert('Failed');
        })
    }

   render(){
       return(
           

           <div className="container">
               
<Modal
{...this.props}
size="lg"
aria-labelledby="contained-modal-title-vcenter"
centered
>
    <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
            Shto Restorantin
        </Modal.Title>
    </Modal.Header>
    <Modal.Body>

        <Row>
            <Col sm={6}>
                <Form onSubmit={this.handleSubmit}>
                    <Form.Group controlId="restaurantID">
                        <Form.Label>Id e restorantit</Form.Label>
                        <Form.Control type="text" name="restaurantID" required 
                        placeholder="ID e restaurantit"/>
                    </Form.Group>
                    <Form.Group controlId="data_regjistrimit">
                                       <Form.Label>Data e regjistrimit</Form.Label>
                                       <Form.Control type="text" name="data_regjistrimit" disabled
                        placeholder="Data e regjistrimit"/>
                    </Form.Group>
                    <Form.Group controlId="perdoruesiID">
                        <Form.Label>ID e perdoruesit</Form.Label>
                        <Form.Control type="text" name="perdoruesiID" required 
                        placeholder="ID e perdoruesit"/>
                    </Form.Group>

                    <Form.Group>
                        <Button variant="primary" type="submit">
                            Shto
                        </Button>
                    </Form.Group>
                </Form>
            </Col>
        </Row>
    </Modal.Body>
    
    <Modal.Footer>
        <Button variant="danger" onClick={this.props.onHide}>Close</Button>
    </Modal.Footer>

</Modal>

           </div>
       )
   }
}
