import React from "react";
import { useEffect, useState } from "react";

const Cart = ({cart, setCart, handleChange}) => {
    const [price, setPrice] = useState(0);

    const handleRemove = (id) => {
        const arr = cart.filter((item) => item.id !== id);
        setCart(arr);
        handlePrice();
    };

    const handlePrice = () => {
        let ans = 0;
        cart.map((item) => (ans += item.cmimi));
        setPrice(ans);
    }

    useEffect(()=>{
        handlePrice();
    })
    return ( 
        <article>
          <thead>
    <tr>
      <th scope="col">#</th>
      <th scope="col">Emertimi</th>
      <th scope="col">shto/largo</th>
      <th scope="col">cmimi per njesi</th>
      <th scope="col">largo</th>
    </tr>
  </thead>
      {cart.map((item) => (
        <tr key={item.menuID}>
        <th scope="row">{item.menuID}</th>
        <td>{item.emertimi}</td>
        <td>
          <button type="button" class="btn btn-light" onClick={() => handleChange(item, 1)}>+</button>
          <button>{item.amount}</button>
          <button type="button" class="btn btn-light" onClick={() => handleChange(item, -1)}>-</button>
        </td>
        <td>{item.cmimi}</td>
        <td><button onClick={() => handleRemove(item.id)}>Remove</button></td>
      </tr>
      ))}
      <div className="total">
        <span>Totali: </span>
        <span>{price} â‚¬</span>
      </div>
    </article>
     );
}
 
export default Cart;

<table class="table">
  
  <tbody>
    
    <tr>
      <th scope="row">2</th>
      <td>Jacob</td>
      <td>Thornton</td>
      <td>@fat</td>
    </tr>
    <tr>
      <th scope="row">3</th>
      <td colspan="2">Larry the Bird</td>
      <td>@twitter</td>
    </tr>
  </tbody>
</table>
