import React, { Component } from 'react';
import { Table } from 'react-bootstrap';


export class Restoranti extends Component {
    constructor(props) {
        super(props);
        this.state = { restorantet: [] }
    }

    //marrja e te dhenave nga API
    refreshList() {
        fetch(process.env.REACT_APP_API + 'restoranti')
            .then(response => response.json())
            .then(data => {
                this.setState({ restorantet: data });
            });
    }

    componentDidMount() {
        this.refreshList();
    }

    componentDidUpdate() {
        this.refreshList();
    }

    render() {
        const { restorantet } = this.state;
        return (
            <div className="mt-5 d-flex justify-content-left">
                <Table className="mt-4" striped bordered hover size="sm">
                    <thead>
                        <tr>
                            <th>Emri</th>
                            <th>Qyteti</th>
                            <th>Adresa</th>
                            <th>Nr.Kontaktues</th>
                            <td>Edit Delete</td>
                        </tr>
                    </thead>
                    <tbody>
                        {restorantet.map(el =>
                            <tr key={el.Id}>
                                <td>{el.emri}</td>
                                <td>{el.qyteti}</td>
                                <td>{el.adresa}</td>
                                <td>{el.nr_kontaktues}</td>
                               

                            </tr>)}
                    </tbody>

                </Table>
            </div>
        )
    }
}
