import React, { useState, useEffect } from 'react'
import { Link, Redirect } from 'react-router-dom';
import AppNavbar from './AppNavbar';
import { Container, Row, Tabs, Tab } from 'react-bootstrap';
import { axiosPrivate } from './api/axios';

const DashboardKlienti = (props) => {
  const [ user, setUser ] = useState();
  const roles = localStorage.getItem('roles');
  const username = localStorage.getItem('username');

  useEffect(() => {
    let isMounted = true;
    const controller = new AbortController();

    const getUsers = async () => {
        try{
            const response = await axiosPrivate.get('/api/authmanagement/user', {
                signal: controller.signal
            });
            console.log(response.data)
            isMounted && setUser(response.data);
        }catch(err){
            console.error(err);
        }
    }

    getUsers();

    return () => {
        isMounted = false;
        controller.abort();
    }
  }, [])

  return (
    <div>
      <AppNavbar />
      <Container className='py-4'>
        <h6>{username}</h6>
        <Row className='justify-content-center'>
          <Tabs justify variant='pills' defaultActiveKey="tab-1" className='mb-1 p-0 border'>
            <Tab eventKey="tab-1" title="Restorantet Favorite">
              Tab 1 Content
              
            </Tab>
            <Tab eventKey="tab-2" title="Te dhenat e mia">
              Tab 2 Content
            </Tab>
            <Tab eventKey="tab-3" title="PorositÃ« e mia">
              Tab 3 Content
            </Tab>
          </Tabs>
        </Row>
      </Container>
    </div>
  )
}

export default DashboardKlienti
