import React from 'react'
import { useEffect, useState } from 'react'

const Favorites = ({ fav, setFav}) => {
    const [price, setPrice] = useState(0);
    //console.log(cart)
    const handleRemove = (id) => {
        // const arr = cart.filter((item) => item.menuID !== id)
        //setCart(arr)
        //handlePrice()
        if (window.confirm('A doni ta largoni restorantin nga favoritet?')) {
            fetch(process.env.REACT_APP_API + 'restfavorite/' + id, {
                method: 'DELETE',
                header: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                }
            })
        }
    }

   // const [favData, setfavData] = useState([]);

    useEffect(() => {
        fetch(process.env.REACT_APP_API + 'restfavorite/' + localStorage.getItem("id"))
            .then(response => response.json())
            .then(data => {
                setFav(data);
            });

    }, [fav]
    )


    return (
        <div>
            <table className="table">
                <tbody>
                    <tr>
                        <th>Emri i restaurantit</th>
                        <th>Numri i yjeve</th>
                        <th>Largo</th>
                    </tr>
                    {fav.map((item) => (
                        <tr key={item.favID}>
                            <th scope="row">{item.restId}</th>
                            <td>{item.restId}</td>
                            <td>
                                <button
                                    type="button"
                                    className='btn btn-danger'
                                    onClick={() => handleRemove(item.favID)}>Remove</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    )
}

export default Favorites
