import React, { Component } from 'react';
import { Table } from 'react-bootstrap';
import { Button, ButtonToolbar } from 'react-bootstrap';
import { AddMenuModal } from './AddMenuModal';
import { EditMenuModal } from './EditMenuModal';
import { Navigation } from './Navigation';


const Mesazhet = () => {
    return ( 
        <div className="restaurant mt-5">
                <Navigation />
                <Table className="mt-4" striped bordered hover size="sm">
                    <thead>
                        <tr>
                            <th>Id</th>
                            <th>Emertimi</th>
                            <th>Cmimi</th>
                            <th>Pershkrimi</th>
                            <th>Nr i Artikujve</th>
                            <th>ID restaurantit</th>
                            <th>ID pija</th>
                            <th>ID ushqimi</th>
                        </tr>
                    </thead>
                    <tbody>
                            <tr>
                                <td></td>
                                <td></td>
                                <td></td>
                                <td>el.pershkrimi}</td>
                                <td>{el.nr_artikujve}</td>
                                <td>{el.restaurantID}</td>
                                <td>{el.pijaID}</td>
                                <td>{el.ushqimiID}</td>
                                <td>
                                    <ButtonToolbar>
                                        <Button className="mr-2" variant="info"
                                            onClick={() => this.setState({
                                                editModalShow: true,
                                                menuID: el.menuID, emertimi: el.emertimi, cmimi: el.cmimi, pershkkrimi: el.pershkrimi, nr_artikujve: el.nr_artikujve
                                            })}>
                                            Edit
                                        </Button>

                                        <Button className="mr-2" variant="danger"
                                            onClick={() => this.deleteMenu(el.menuID)}>
                                            Delete
                                        </Button>

                                        <EditMenuModal show={this.state.editModalShow}
                                            onHide={editModalClose}
                                            menuID={menuID}
                                            emertimi={emertimi}
                                            cmimi={cmimi}
                                            pershkrimi={pershkrimi}
                                            nr_artikujve={nr_artikujve}
                                        />
                                    </ButtonToolbar>

                                </td>

                            </tr>)}
                    </tbody>

                </Table>
                <ButtonToolbar>
                    <div className="d-grid gap-2">
                        <Button variant='primary' size='lg'
                            onClick={() => { this.setState({ addModalShow: true }) }}>
                            Shto Menu
                        </Button>
                    </div>
                    <AddMenuModal show={this.state.addModalShow}
                        onHide={addModalClose}
                    />
                </ButtonToolbar>
            </div>
     );
}
 
export default Mesazhet;
