import React, { Component } from 'react';
import { Table } from 'react-bootstrap';
import { Button, ButtonToolbar } from 'react-bootstrap';
import { AddRoleModal } from './AddRoleModal';
import { EditRoleModal } from './EditRoleModal';
import { Navigation } from './Navigation';


export class Telefoni extends Component {
    constructor(props) {
        super(props);
        this.state = { numbers: [], addModalShow: false }
    }

    //marrja e te dhenave nga API
    refreshList() {
        fetch(process.env.REACT_APP_API + 'telefoni')
            .then(response => response.json())
            .then(data => {
                this.setState({ numbers: data });
            });
    }

    componentDidMount() {
        this.refreshList();
    }

    componentDidUpdate() {
        this.refreshList();
    }

    //delete a user
    deletePhoneNumber(id) {
        if (window.confirm('A doni ta fshini numrin e telefonit?')) {
            fetch(process.env.REACT_APP_API + 'telefoni/' + id, {
                method: 'DELETE',
                header: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                }
            })
        }
    }

    render() {
        const { numbers, numri, userId, Nr_tel } = this.state;
        let addModalClose = () => this.setState({ addModalShow: false });
        let editModalClose = () => this.setState({ editModalShow: false });
        return (
            <div className="role mt-5">
                <Navigation />
                <Table className="mt-4" striped bordered hover size="sm">
                    <thead>
                        <tr>
                            <th>Nr i telefonit</th>
                            <th>Id e perdoruesit</th>
                        </tr>
                    </thead>
                    <tbody>
                        {numbers.map(el =>
                            <tr key={el.numri}>
                                <td>{el.Nr_tel}</td>
                                <td>{el.userId}</td>
                                <td>


                                </td>

                            </tr>)}
                    </tbody>

                </Table>
                <ButtonToolbar>


                </ButtonToolbar>
            </div>
        )
    }
}
