import React, { Component } from 'react';
import { Table } from 'react-bootstrap';

import {Button,ButtonToolbar} from 'react-bootstrap';

export class Restoranti extends Component {
    constructor(props) {
        super(props);
        this.state = { restorantet: [] }
    }

    //marrja e te dhenave nga API
    refreshList() {
        fetch(process.env.REACT_APP_API + 'restoranti')
            .then(response => response.json())
            .then(data => {
                this.setState({ restorantet: data });
            });
    }

    componentDidMount() {
        this.refreshList();
    }

    componentDidUpdate() {
        this.refreshList();
    }

    render() {
        const { restorantet } = this.state;
        return (
            <div className="mt-5 d-flex justify-content-left">
                Restorantet.
                <Table className="mt-4" striped bordered hover size="sm">
                    <thead>
                        <tr>
                            <th>DepartmentId</th>
                            <th>DepartmentName</th>
                            <th>Options</th>
                        </tr>
                    </thead>
                    <tbody>
                        {restorantet.map(dep =>
                            <tr key={dep.Id}>
                                <td>{dep.Emri}</td>
                                <td>{dep.Adresa}</td>
                                <td>
                                    <ButtonToolbar>
                                        <Button className="mr-2" variant="info">Edit</Button>

                                        <Button className="mr-2" variant="danger">Delete</Button>


                                    </ButtonToolbar>

                                </td>

                            </tr>)}
                    </tbody>

                </Table>
            </div>
        )
    }
}
