import React from 'react';
import './App.css';
import { useEffect, useState } from "react";

import { Restoranti } from './Restoranti';
import { Qyteti } from './Qyteti';
import { Menu } from './Menu';
import { Roli } from './Roli';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import LoginForm from './LoginForm';
import { TransportuesiRegister } from './TransportuesiRegister';
import { Restaurant_Qyteti } from './Restaurant_Qyteti';
import { Perdoruesi } from './Perdoruesi';
import RestorantiRegister from './RestorantiRegister';
import DashboardRestoranti from './DashboardRestoranti';
import AppNavbar from './AppNavbar';
import { Redirect } from 'react-router-dom'



function App() {
    const [name, setName] = useState('');
    const [success, setSuccess] = useState(false);
    const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [redirect, setRedirect] = useState(false)
  const [role, setRole] = useState('')

  useEffect(()=>{
    (
        async () => {
            const response = await fetch(process.env.REACT_APP_API+'authmanagement/user',{
                headers:{
                    'Accept':'application/json',
                    'Content-Type':'application/json'
                },
                credentials: 'include'
            });

            const content = await response.json();
            setName(content.userName);
        }
    )()
}, [name]);

  const submit = async (event) => {
    event.preventDefault()

    await fetch(process.env.REACT_APP_API + 'authmanagement/login', {
      method: 'POST',
      headers: {
        Accept: 'application/json',
        'Content-Type': 'application/json',
      },
      credentials: 'include',
      body: JSON.stringify({
        email,
        password,
      }),
    })
      .then((res) => res.json())
      .then(
        (result) => {
          fetch(process.env.REACT_APP_API + 'setup/GetUserRoles?email='+email, {
            method: 'GET',
            headers: {
              Accept: '*/*'
            },
            credentials: 'include',
          }).then(response => response.text())
          .then(data => {
            let role = data.slice(2,data.length-2);
            
            setRole(role);
          });
          setRedirect(true)
        },
        (error) => {
          alert('Failed')
        },
      )
  }


  if (redirect) {
    return <Redirect to="restorantidashboard" />
  }
    
    
    
    return (
        <Router>
            <div className="App container">
                <AppNavbar name={name} setName={setName}/>
                <div className="d-flex justify-content-center">
      <form onSubmit={submit} autoComplete="off">
        <div className="w-90">
          <h2>Login</h2>

          <div className="form-outline mb-8">
            <input
              type="email"
              id="email"
              className="form-control"
              name="email"
              required
              onChange={(e) => setEmail(e.target.value)}
            />
            <label className="form-label" for="email">
              Email address
            </label>
          </div>

          <div className="form-outline mb-4">
            <input
              type="password"
              id="password"
              className="form-control"
              name="password"
              required
              onChange={(e) => setPassword(e.target.value)}
            />
            <label className="form-label" for="password">
              Password
            </label>
          </div>

          <div className="row mb-4">
            <div className="col d-flex justify-content-center">
              <div className="form-check">
                <input
                  className="form-check-input"
                  type="checkbox"
                  value=""
                  id="checkbox"
                  checked
                />
                <label className="form-check-label"> Remember me </label>
              </div>
            </div>

            <div className="col">
              <a href="#!">Forgot password?</a>
            </div>
          </div>

          <button
            type="submit"
            value="LOGIN"
            className="btn btn-primary btn-block mb-4"
          >
            Sign in
          </button>

          <div className="text-center">
            <p>
              Not a member? <a href="#!">Register</a>
            </p>
            <p>or sign up with:</p>
            <button type="button" className="btn btn-link btn-floating mx-1">
              <i className="fab fa-facebook-f"></i>
            </button>

            <button type="button" className="btn btn-link btn-floating mx-1">
              <i className="fab fa-google"></i>
            </button>

            <button type="button" className="btn btn-link btn-floating mx-1">
              <i className="fab fa-twitter"></i>
            </button>

            <button type="button" className="btn btn-link btn-floating mx-1">
              <i className="fab fa-github"></i>
            </button>
          </div>
        </div>
      </form>
    </div>
                    <Switch>
                    <Route path="/restaurant" component={Restoranti} />
                    <Route path="/qyteti" component={Qyteti} />
                    <Route path="/menu" component={Menu} />
                    <Route path="/role" component={Roli} />
                    <Route path="/restaurant_qyteti" component={Restaurant_Qyteti} />
                    <Route exact path="perdoruesi" component={Perdoruesi} />
                    <Route path="/transportuesiregister" component={TransportuesiRegister} />
                    <Route path="/restorantiregister" component={RestorantiRegister} />
                    <Route path="/restorantidashboard" component={() => <DashboardRestoranti name={name}/>} />
                    </Switch>
            </div>
        </Router>
    );
}

export default App;
