import React, { useState } from 'react';
import { Redirect } from 'react-router-dom';

function LoginForm(props) {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [redirect, setRedirect] = useState(false); 

    const submit = async (event) =>{
        event.preventDefault();

        const response = await fetch(process.env.REACT_APP_API+'authmanagement/login',{
            method:'POST',
            headers:{
                'Accept':'application/json',
                'Content-Type':'application/json'
            },
            credentials: 'include',
            body:JSON.stringify({
                email,
                password
            })
        });

        setRedirect(true);
    }

    if(redirect){
        return <Redirect to='restorantidashboard' />
    }
    return (
        <div className="d-flex justify-content-center">
            <form onSubmit={submit}>
                <div className="w-90">
                    <h2>Login</h2>
  
                    <div className="form-outline mb-8">
                        <input type="email" id="email" className="form-control" name="email" required onChange={e=>setEmail(e.target.value)}/>
                        <label className="form-label" for="email">Email address</label>
                    </div>

                    <div className="form-outline mb-4">
                        <input type="password" id="password" className="form-control" name="password" required onChange={e=>setPassword(e.target.value)}/>
                        <label className="form-label" for="password">Password</label>
                    </div>

                    <div className="row mb-4">
                        <div className="col d-flex justify-content-center">
                            <div className="form-check">
                                <input className="form-check-input" type="checkbox" value="" id="checkbox" checked />
                                <label className="form-check-label"> Remember me </label>
                            </div>
                        </div>

                        <div className="col">
                            <a href="#!">Forgot password?</a>
                        </div>
                    </div>

                    <button type="submit" value="LOGIN" className="btn btn-primary btn-block mb-4">Sign in</button>


                    <div className="text-center">
                        <p>Not a member? <a href="#!">Register</a></p>
                        <p>or sign up with:</p>
                        <button type="button" className="btn btn-link btn-floating mx-1">
                            <i className="fab fa-facebook-f"></i>
                        </button>

                        <button type="button" className="btn btn-link btn-floating mx-1">
                            <i className="fab fa-google"></i>
                        </button>

                        <button type="button" className="btn btn-link btn-floating mx-1">
                            <i className="fab fa-twitter"></i>
                        </button>

                        <button type="button" className="btn btn-link btn-floating mx-1">
                            <i className="fab fa-github"></i>
                        </button>
                    </div>
                </div>
            </form>
        </div>
    )
}

export default LoginForm;
