        let editModalClose = () => this.setState({ editModalShow: false });*/
        return (
            <div className="restaurant mt-5">
                <Table className="mt-4" striped bordered hover size="sm">
                    <thead>
                        <tr>
                            <th>Nr i artikujve</th>
                            <th>Totali i fatures</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <td>2</td>
