import React, { useState } from 'react'

function LoginForm({ Login, error }) {
    const [details, setDetails] = useState({ name: "", email: "", password: "" });
    const submitHandler = e => {
        e.preventDefault();
        Login(details);
    }
    return (
        <div>
            <form onSubmit={submitHandler}>
                <div className="form-inner container-sm">
                    <h2>Login</h2>
                    {(error != "") ? (<div className='error'>{error}</div>) : ""}
                    <div className='col-md-6 form-group input-group mb-3' >
                        <label htmlFor='name' className="col-md-4"></label>
                        <input type="text" className="form-control" name="name" id="name" onChange={e => setDetails({ ...details, name: e.target.value })} value={details.name} />
                    </div>
                    <div className='form-group input-group mb-3'>
                        <label htmlFor='email' className="col-sm-2 col-form-label col-form-label-sm">Email:</label>
                        <input type="email" className="form-control" name="email" id="email" onChange={e => setDetails({ ...details, email: e.target.value })} value={details.email} />
                    </div>
                    <div className='form-group input-group mb-3'>
                        <label htmlFor='password' className="col-sm-2 col-form-label">Password:</label>
                        <input type="password" className="form-control" name="password" id="password" onChange={e => setDetails({ ...details, password: e.target.value })} value={details.password} />
                    </div>
                    <input className="btn btn-primary" type="submit" value="LOGIN" />
                </div>
            </form>
        </div>
    )
}

export default LoginForm;
